class Piece{
  constructor(size){
    this.size = size;
    this.positions = [];
    this.hits = [];
    this.alive = true;
    this.placed = false;
  }
}

class PlayerPieces{
  constructor(){
    this.gamePieces = [new Piece(2), new Piece(3), new Piece(3), new Piece(4), new Piece(5)];
  }
  canPlaceGamepiece(arr){
    let canPlace = true;
    for(let i = 0; i < this.gamePieces.length; i++){
      let piece = this.gamePieces[i];
      for(let j = 0; j < arr.length; j++){
        if(piece.includes(arr[j])){
          canPlace = false;
          break;
        }
      }
    }
    return canPlace;
  }
}

let playerPieces = new PlayerPieces()


export default playerPieces;
